<!DOCTYPE html><html style="height: 100%"><head>    <meta charset="utf-8"></head><body style="height: 100%; margin: 0"><div id="container" style="height:600px;width: 1000px;margin-top: 5px"></div><script type="text/javascript" src="{{secure_asset('resources/echarts/echarts.min.js')}}"></script><script type="text/javascript">    var dom = document.getElementById("container");    var myChart = echarts.init(dom);    var app = {};    app.title = '用户最近七天学习情况可视化分析';    option = {        tooltip: {            trigger: 'axis',            axisPointer: {                type: 'cross',                crossStyle: {                    color: '#999'                }            }        },        toolbox: {            feature: {                dataView: {show: true, readOnly: false},                magicType: {show: true, type: ['line', 'bar']},                restore: {show: true},                saveAsImage: {show: true}            }        },        legend: {            data:['视频学习时长','资料学习时长','平均学习时长']        },        xAxis: [            {                type: 'category',                data: ['2019-05-21','2019-05-22','2019-05-23','2019-05-24','2019-05-25','2019-05-26','2019-05-27'],                axisPointer: {                    type: 'shadow'                }            }        ],        yAxis: [            {                type: 'value',                name: '视频学习时长',                min: 0,                max: 250,                interval: 50,                axisLabel: {                    formatter: '{value}分钟'                }            },            {                type: 'value',                name: '资料学习时长',                min: 0,                max: 150,                interval: 15,                axisLabel: {                    formatter: '{value}分钟'                }            }        ],        series: [            {                name:'视频学习时长',                type:'bar',                data:[50,160,120,150,60,200,240]            },            {                name:'资料学习时长',                type:'bar',                data:[20,50,60,90,45,35,40]            },            {                name:'平均学习时长',                type:'line',                yAxisIndex: 1,                data:[35,105,90,120,55,120,140]            }        ]    };    ;    if (option && typeof option === "object") {        myChart.setOption(option, true);    }</script></body></html>